// File: src/components/Cart.jsx
import React from 'react';

export default function Cart({ cart, saveCart, setCart }) {
  // Remove everything
  const clear = () => {
    saveCart({});
    setCart({});
  };

  // Remove one unit of an item, or delete it if qty goes to zero
  const decrement = (id) => {
    const item = cart[id];
    if (!item) return;

    let newCart = { ...cart };

    if (item.qty > 1) {
      newCart[id] = { ...item, qty: item.qty - 1 };
    } else {
      // qty is 1 → remove item entirely
      delete newCart[id];
    }

    saveCart(newCart);
    setCart(newCart);
  };

  // (Optional) If you ever want an increment button
  const increment = (id) => {
    const item = cart[id];
    if (!item) return;

    const newCart = {
      ...cart,
      [id]: { ...item, qty: item.qty + 1 }
    };
    saveCart(newCart);
    setCart(newCart);
  };

  // Compute total
  const total = Object.values(cart)
    .reduce((sum, { price, qty }) => sum + price * qty, 0)
    .toFixed(2);

  return (
    <main className="cart-page container">
      <h1>Your Order</h1>
      <table className="cart-table">
        <thead>
          <tr>
            <th>Item</th>
            <th>Price</th>
            <th>Qty</th>
            <th>Subtotal</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {Object.entries(cart).map(([id, { name, price, qty }]) => (
            <tr key={id}>
              <td>{name}</td>
              <td>${price.toFixed(2)}</td>
              <td>
                <button
                  className="qty-btn"
                  onClick={() => decrement(id)}
                  aria-label={`Remove one ${name}`}
                >
                  –
                </button>
                <span className="qty-number">{qty}</span>
                <button
                  className="qty-btn"
                  onClick={() => increment(id)}
                  aria-label={`Add one ${name}`}
                >
                  +
                </button>
              </td>
              <td>${(price * qty).toFixed(2)}</td>
              <td>
                <button
                  className="remove-btn"
                  onClick={() => {
                    // remove entire line item
                    const newCart = { ...cart };
                    delete newCart[id];
                    saveCart(newCart);
                    setCart(newCart);
                  }}
                  aria-label={`Remove ${name} entirely`}
                >
                  ×
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <div className="cart-summary">
        <span className="cart-total">Total: ${total}</span>
        <button className="btn clear-btn" onClick={clear}>
          Clear Cart
        </button>
        <button
          className="btn order-btn"
          onClick={() => alert('Order placed!')}
        >
          Place Order
        </button>
      </div>
    </main>
  );
}
