import React, { useState, useEffect } from 'react';
import { loadCart, saveCart } from '../utils/cart';
import { Link } from 'react-router-dom';

export default function Cart() {
  const [cart, setCart] = useState({});

  useEffect(() => setCart(loadCart()), []);

  const remove = name => {
    const c = { ...cart };
    delete c[name];
    saveCart(c);
    setCart(c);
  };

  const clear = () => {
    saveCart({});
    setCart({});
  };

  const total = Object.values(cart).reduce((sum, { price, qty }) => sum + price * qty, 0);

  return (
    <main className="cart-page container">
      <h1>Your Order</h1>
      <table className="cart-table">
        <thead>
          <tr>
            <th>Item</th><th>Price</th><th>Qty</th><th>Subtotal</th><th>Remove</th>
          </tr>
        </thead>
        <tbody>
          {Object.entries(cart).map(([name, { price, qty }]) => (
            <tr key={name}>
              <td>{name}</td>
              <td>${price.toFixed(2)}</td>
              <td>{qty}</td>
              <td>${(price * qty).toFixed(2)}</td>
              <td><button className="remove-item" onClick={() => remove(name)}>&times;</button></td>
            </tr>
          ))}
        </tbody>
      </table>
      <div className="checkout-summary">
        <span>Total: ${total.toFixed(2)}</span>
        <button className="clear-cart" onClick={clear}>Clear Cart</button>
        <Link to="/" className="hero-order-btn" onClick={clear}>Place Order</Link>
      </div>
    </main>
  );
}
