import React from 'react';
import { loadCart, saveCart } from '../utils/cart';

const items = [
  { name: 'Bruschetta', price: 8.50 },
  { name: 'Calamari', price: 10.00 },
  { name: 'Margherita Pizza', price: 14.00 },
  { name: 'Fettuccine Alfredo', price: 16.50 },
  { name: 'Tiramisu', price: 7.00 },
  { name: 'Panna Cotta', price: 6.50 }
];

export default function Menu() {
  const addToCart = item => {
    const cart = loadCart();
    cart[item.name] = cart[item.name] || { price: item.price, qty: 0 };
    cart[item.name].qty++;
    saveCart(cart);
  };

  return (
    <main className="menu-page container">
      <h1>Our Menu</h1>
      <div className="menu-items">
        {items.map(item => (
          <div key={item.name} className="menu-card">
            <h3>{item.name}</h3>
            <p className="price">${item.price.toFixed(2)}</p>
            <button className="add-to-cart" onClick={() => addToCart(item)}>+ Add</button>
          </div>
        ))}
      </div>
    </main>
  );
}
